import 'package:dio/dio.dart';import 'package:prankers/adModule/AdConfig.dart';import 'package:prankers/adModule/AdManager.dart';class ApiService {  static const String _baseAdUrl = 'https://aani-app-manager.s3.ap-south-1.amazonaws.com';  final Dio _dioAd = Dio(    BaseOptions(      baseUrl: _baseAdUrl,      connectTimeout: const Duration(seconds: 10),      receiveTimeout: const Duration(seconds: 10),      sendTimeout: const Duration(seconds: 10),      headers: {'Accept': 'application/json', 'Content-Type': 'application/json'},    ),  );  Future<bool> fetchAdConfig(String path) async {    final response = await _dioAd.get("/$path/ad_manager.json");    if (response.statusCode == 200) {      final data = response.data;      final config = AdConfig.fromJson(data);      AdManager.instance?.setAdConfig(config);      return true;    } else {      return false;    }  }}