import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import '../../extensions/app_colors.dart';import '../../extensions/app_text_styles.dart';class SelectableChip extends StatefulWidget {  final String label;  final Function(bool selected) onSelected;  final bool initiallySelected;  const SelectableChip({    Key? key,    required this.label,    required this.onSelected,    required this.initiallySelected,  }) : super(key: key);  @override  State<SelectableChip> createState() => _SelectableChipState();}class _SelectableChipState extends State<SelectableChip> {  late bool isSelected;  @override  void initState() {    super.initState();    isSelected = widget.initiallySelected;  }  void _toggleSelection() {    setState(() {      isSelected = !isSelected;      widget.onSelected(isSelected);    });  }  @override  Widget build(BuildContext context) {    return Padding(      padding: EdgeInsets.symmetric(vertical: 4.0),      child: GestureDetector(      onTap: _toggleSelection,      child: Chip(        label: Text(          widget.label,          style: AppTextStyles.of(context).headline5.copyWith(            color: isSelected ? colorAccentText : colorCardText,          ),        ),        backgroundColor: isSelected ? colorAccent : colorCard.withAlpha(125),      ),    ),    );  }}